create procedure dbo.USP_DATAFORMTEMPLATE_ADD_OUTLOOK_INTEGRATION_TAG ( @ID uniqueidentifier = null output, @MESSAGEID nvarchar(500), @INTERACTIONID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null ) as
set nocount on;
if @ID is null set @ID = newid()
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try declare @existingID uniqueidentifier; select @existingID = ID from dbo.USR_OUTLOOK_INTEGRATION_TAG where (MESSAGEID = @MESSAGEID);
if @existingID is null insert into dbo.USR_OUTLOOK_INTEGRATION_TAG (ID, MESSAGEID, INTERACTIONID, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) values (@ID, @MESSAGEID, @INTERACTIONID, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE) else begin set @ID = @existingID; update dbo.USR_OUTLOOK_INTEGRATION_TAG set INTERACTIONID=@INTERACTIONID where ID=@ID; end
end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
